INSERT INTO `crm_request_manage` VALUES (70, 2020, 5, '师资运营', 'CRM', 1204, '杨苗苗', '结束试听勾选了老师原因同步到教务投诉处理列表', '入口：学生主页-试听记录-试听结束\n需求：客户试听课无论完成还是未完成，只要是老师原因的都展示在教务-投诉处理下面\n投诉类型为：试听课投诉\n试听课时间、学生姓名ID 老师姓名ID点击都可跳转到主页\n投诉内容展示勾选的家长是否满意，老师原因，原因详情\n投诉时间提交结束试听时间\n投诉人：结束试听对应的操作人\n所属教务：老师对应的专属教务\n各种操作不变', 2, '2020-05-01', NULL, '2020-05-11', '2020-05-14', '2020-05-08 12:15:31', 1, 3, NULL, NULL);
