INSERT INTO `crm_request_manage` VALUES (73, 2020, 5, '研发中心', 'CRM', 72, '谭杰', '需求管理优化第三期', '1. 使用异步任务，每隔一个小时去同步一下所有的需求状态。\n1.1 只要有一个子任务不在【待处理】就更改需求状态为【进行中】。\n1.2 所有的子任务的状态，是【取消】【完成】的状态，更改需求状态为【已完成】。\n2. 执行时间：8-23 每小时执行一次。', 2, '2020-05-08', NULL, '2020-05-09', '2020-05-09', '2020-05-08 13:55:28', 1, 2, NULL, NULL);
