INSERT INTO `crm_request_manage` VALUES (75, 2020, 5, '教务', 'CRM', 1204, '杨苗苗', '兼职工资优化【额外】', '1.兼职老师每个月都要记录老师身份和发放状态，且可以查询到之前月份的工资，且支持下载\n例：3月份之前老师身份都是兼职，4月转为全职，在查询2月 3月老师工资时仍可查询到是兼职时的工资\n2.如果老师当月多次调整身份，以最后一次身份为准展示工资\n例：4月老师全职转为兼职，兼职又转为全职，该老师工资仅在全职列表展示，具体工资计算由教务手动核对计算', 2, '2020-05-01', NULL, '2020-05-13', '2020-05-14', '2020-05-08 14:07:54', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (76, 2020, 5, '市场', 'CRM', 453, '毛家俊', '拼团线索分配（额外）', '1、用户开团成功后，若该用户无持有顾问，则用户（客户）分配给周城（入微）id:1191 (zhoucheng) 【原分配给赖家伟】\n原任务：CRM-1727\n2、当客户分配完成后，新增钉钉消息推送周城：“拼团用户 【用户名】完成支付，请及时跟进分配。”', 2, '2020-05-01', NULL, '2020-05-13', '2020-05-14', '2020-05-08 14:10:43', 1, 2, NULL, NULL);
