INSERT INTO `crm_request_manage` VALUES (117, 2020, 5, '市场部', '官网H5', 453, '毛家俊', '投放渠道新增统计代码（紧急）', '需求描述：市场与渠道方对接，进行市场投放，需进行页面数据统计\n投放链接：https://m.fudaojun.com/yingxiao/launchPage.html?channel=baidusem\n新增百度统计代码如下：\n<script>\nvar _hmt = _hmt || [];\n(function() {\n  var hm = document.createElement(\"script\");\n  hm.src = \"https://hm.baidu.com/hm.js?f18e049f2740381915d18cdb8a7ad0a4\";\n  var s = document.getElementsByTagName(\"script\")[0]; \n  s.parentNode.insertBefore(hm, s);\n})();\n</script>\n', 2, '2020-05-15', NULL, '2020-05-15', '2020-05-15', '2020-05-15 10:30:06', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (118, 2020, 5, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-需求管理下新增需求池模块【优先做】', 'CRM-需求管理下新增需求池模块\n需求：便于其他部门的人提需求，详情见附件，最好下周上线', 2, '2020-05-15', NULL, '2020-05-20', '2020-05-28', '2020-05-15 18:18:29', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (119, 2020, 5, '研发部', 'H5客户端', 1413, '黄磊', '作业、错题排序', '需求：pc客户端（老师+学生）“作业” “错题”两个板块所有状态下的【截止时间】排序改为时间倒序，最新的时间排在最上面\n\n1.作业：“未批改”  “未完成” “已批改”\n2.错题：“作业错题”  ”刷题错题“', 1, '2020-05-15', NULL, '2020-05-18', '2020-05-21', '2020-05-15 18:44:04', 0, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (120, 2020, 6, '花信', '花信后台', 453, '毛家俊', '【1.8.3】花信后台-店铺管理', '需求描述：现APP端支持用户填写申请、支付开店费用后完成开店，因此对后台店铺管理及详情内容进行优化\n1、店铺列表新增展示字段；\n2、店铺详情新增填写内容及优化；\n3、新增分销团队查看；', 2, '2020-05-17', NULL, '2020-05-25', '2020-05-28', '2020-05-17 19:47:50', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (121, 2020, 6, '花信', '花信后台', 453, '毛家俊', '【1.8.3】花信后台-商品管理', '需求描述：现上架分销商城模块，因此需迭代商品管理列表，提供平台对店主商品进行选品上架处理；\n功能描述：\n1、商品管理位于店铺管理下方，用于管理/查看已创建的商品信息；\n2、新增商品列表展示，支持零售价、销量、库存、佣金、创建时间进行正序/倒序排序；\n3、支持添加/取消品牌商品；', 2, '2020-05-17', NULL, '2020-05-25', '2020-05-28', '2020-05-17 19:52:05', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (122, 2020, 6, '花信', '花信后台', 453, '毛家俊', '【1.8.3】订单管理', '需求描述：因线上分销商品店铺都为花信进行管理，为方便花信后台发货操作，需在订单中新增发货入口；\n功能点：\n1、支持平台在后台进行快速发货处理；\n2、记录发货信息；\n3、区分订单类型：自营订单/分销订单；', 2, '2020-05-17', NULL, '2020-05-25', '2020-05-28', '2020-05-17 19:54:22', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (123, 2020, 5, '研发部', 'CRM', 1204, '杨苗苗', '学生主页-发起试听', '发起试听时，试听类型为【老生更换老师】，系统推荐时，自动过滤掉当前对应学科绑定的原老师，不让原老师看到这个试听单\n例：学生张林文煜 ID1737280，发起【老生更换老师】学科为英语，现在绑定的英语老师为张馨予，现在张馨予可以收到这个试听单推荐且可以申请，改为老师张馨予不能看到这个试听单\n', 2, '2020-05-18', NULL, '2020-05-20', '2020-05-28', '2020-05-18 10:19:25', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (124, 2020, 5, '市场部', 'CRM', 453, '毛家俊', 'CRM-转介绍关系数据库操作', '需求描述：用户（沈含瑜1718450）通过用户（沈含桐1718043）介绍，但其在其他的渠道进行注册，现市场同意将用户更改为转介绍，并关联介绍人。\n【需操作数据库关联双方关系】\n关联人员如下：\n介绍人：沈含桐1718043\n被介绍人：沈含瑜1718450\n', 2, '2020-05-18', NULL, '2020-05-19', '2020-05-20', '2020-05-18 15:15:46', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (125, 2020, 5, '教务部', 'Java后台', 453, '毛家俊', '短信内容推送更改', '对外推送的短信内容中，老师的姓名需更改为【花名】，而非真实姓名。\n状态：排课成功/修改排课/取消课程/老师完成评价中，给用户推送的短信中，老师的真实姓名用花名进行替代\n例子：取消课程短信：【辅导君】05-02 15:00的【老师花名】的XX课程已取消，如果疑问请与班主任或老师沟通。', 0, '2020-05-18', NULL, '2020-05-19', '2020-05-19', '2020-05-18 16:41:27', 0, 2, NULL, NULL);
