INSERT INTO `crm_request_manage` VALUES (186, 2020, 6, '投放部', 'H5+Java后台', 453, '毛家俊', '支付宝小程序', '需求背景: 近期蚂蚁金服定向招募在线教育优质机构，通过公司投资人牵线，联系支付宝教育板块对接负责人，经过前期沟通，可以给我们加入到内测名单中，给到百万曝光流量支持。\n需求描述: 后期支付宝小程序降是阿里系流量主要的落地页，需要：开发支付宝小程序。功能点：1、官网信息展示；2、优质课程列表并支持购课（该任务另外单独建）；3、教学中心；\n基本介绍：  https://opendocs.alipay.com/mini/introduce\n开放能力：  https://opendocs.alipay.com/mini/00am3f\n技术开发：  https://opendocs.alipay.com/mini/developer', 2, '2020-06-01', '2020-06-03', '2020-06-10', '2020-06-12', '2020-05-29 14:45:31', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (187, 2020, 6, '市场部', 'H5前端+Java后台', 453, '毛家俊', '服务号—购课抽奖活动', '需求背景: 抽奖活动为每月常规活动，需对活动内容进行更换\n需求描述: \n1、抽奖活动页面主题&活动规则更换（前端页面）；\n2、抽奖活动奖品更换，并修改中奖概率（奖品图片+概率，数量=概率*1000）；\n3、抽奖活动每个月的兑换时间更改，原本月购课仅限本月抽奖，需更改为本月购课，可抽奖时间延长2天，至次月2号24:00。例子：6月购课满30课时，6月未进行抽奖，支持用户7月2号24:00前进行抽奖。若7月再次购课，7月当月仍有抽奖机会。', 2, '2020-06-02', '2020-06-03', '2020-06-04', '2020-06-05', '2020-05-29 14:46:20', 1, 3, NULL, NULL);
