INSERT INTO `crm_request_manage` VALUES (195, 2020, 6, '研发部', '学生app', 1413, '黄磊', '知识点分年级', '优化位置见附件：\n1.语文，数学，英语，知识点分年级取题（先从数据库跑数据）\n科目：语文 数学 英语\n学段：小学 初中 高中\n2.每个年级有哪些知识点（有些年级知识点可能重合，年级不用分上下）\n\n①小学：一年级 二年级 三年级 四年级 五年级 六年级（语文、数学、外语）\n②初中：七年级 八年级 九年级（语文、数学、外语）\n③高中： 高一 高二 高三（语文、数学、外语）\n\n3.每个年级知识点下面有哪些题目', 2, '2020-05-01', NULL, '2020-06-15', '2020-06-18', '2020-05-30 15:23:37', 1, 3, NULL, NULL);
