INSERT INTO `crm_request_manage` VALUES (208, 2020, 6, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-报表-报表下载【优先】', '报表下载数据权限调整\n销售总监可查看的报表：\n1.下载顾问行动力得分\n2.活跃学生数据\n3.学生生量下载\n4.顾问通话时长\n5.顾问学生数量统计\n教务总监&教务可查看报表：\n1.科大讯飞-课后评价下载\n2.老师周课耗详情\n3.教师资格证\n财务&财务总监可查看报表：\n1.老师课时收入详情\n2.课程支出详情\n\n学管&客服或客服学管总监可查看到的报表：\n1.活跃学生数据\n2.学生生量下载\n辅导君权限组可以看到所有数据\n', 2, '2020-06-01', NULL, '2020-06-04', '2020-06-11', '2020-06-01 10:55:17', 1, 3, NULL, NULL);
