INSERT INTO `crm_request_manage` VALUES (212, 2020, 8, '研发部', 'CRM', 453, '毛家俊', '代理商工作台（数据看板）', '需求背景：代理商业务作为年度工作核心，需支持对代理商的账号管理及数据管理。\n功能点：\n代理工作台（数据看板）：统计展示代理商的整体概况\n1、销售额统计；\n2、业绩排行榜；\n3、代理进度图\n4、全国代理分布图；\n5、代理概述\n6、转介绍概述；\n7、代理年段成单概述；\n', 2, '2020-07-08', '2020-07-10', '2020-08-05', '2020-08-10', '2020-06-01 20:18:07', 1, 3, NULL, NULL);
