INSERT INTO `crm_request_manage` VALUES (233, 2020, 6, '研发部', 'jAVA后台 ', 1413, '黄磊', '试听课申请补贴限制', '试听课学生一直未进入过教室，点击下课按钮时，需要检测教室内是否存在课件（若老师上传了又全部删除，则判定为不存在课件）：\n①存在课件：按照原有流程走，点击下课按钮时，提示“去app申请试听课补贴”\n②不存在课件：点击下课按钮时，不提示“去app申请试听课补贴”，直接跳转到 待上课程 页面（已上课程 中不显示该节课），老师APP上的该节课由 “进行中”变为“已取消 ”课程详情不显示申请试听补贴按钮', 0, '2020-06-04', NULL, '2020-06-17', '2020-06-19', '2020-06-04 14:32:45', 0, 3, NULL, NULL);
