INSERT INTO `crm_request_manage` VALUES (235, 2020, 6, '研发部', 'QT客户端', 1413, '黄磊', '录音上传优化', '出现时机\n1、老师上完课，点击下课后，若此时关闭客户端，进行弹窗提示\n2、登陆账号后，立即点击关闭客户端，如果有3小时之前未上传录音文件，进行上传并提示\n\n\n提示文字：“录音上传中”，并显示上传进度条\n上传完成后自动关闭，点击关闭按钮立即关闭', 2, '2020-06-04', NULL, '2020-06-14', '2020-06-15', '2020-06-04 15:27:23', 1, 3, NULL, NULL);
