INSERT INTO `crm_request_manage` VALUES (248, 2020, 6, '学管部', 'CRM', 1204, '杨苗苗', '学管与销售部数据手动流转【紧急】', '1.6月11号学管与销售部进行沉睡数据手动流转，11号流转完后15号不用再进行流转\n2.下个月还是按照1号 15号自动流转\n3.学管和销售人员已配置，11号进行数据流转前麻烦告知一下，需要提前和销售学管说\n沉睡定义见之前需求CRMCRM-2205', 2, '2020-06-09', NULL, '2020-06-10', '2020-06-11', '2020-06-09 16:56:08', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (249, 2020, 6, '研发部', 'CRM', 1204, '杨苗苗', '公司管理-人员管理-重置密码文案优化', '背景：之前账号密码重置是发送到企微的，现在重置的密码发送到钉钉，但提示文案没有同步修改\n所以重置密码之后，文案提示改为“密码已成功发送至【***】的钉钉', 2, '2020-06-09', NULL, '2020-06-16', '2020-06-18', '2020-06-09 18:01:01', 1, 2, NULL, NULL);
