INSERT INTO `crm_request_manage` VALUES (265, 2020, 6, '投放部', 'H5', 453, '毛家俊', '辅导君新域名需求', '需求背景: 公司搜索引擎主要靠SEM推广，SEO效果无法检测。预测SEO效果优于SEM，但是无法识别追踪效果，故需要生成一个单独的域名来投放。\n需求描述: 近期在做SEO优化，高价核心词可以优化到首页，但现在无法单独识别效果，故需要公司新建一个域名来配合做SEO优化，便于检测效果。\n期望结果: SEO优化的成本低于关键词竞价，期望通过SEO优化的方式将高价热门词拍到首页带来自然流程，线索注册成本可以低于SEM竞价，给公司拓展新的推广模式。\n\n功能描述：\n1、使用不同于m.fudaojun.com的1级域名，开发一个投放页。页面样式与功能同步连接：https://m.fudaojun.com/yingxiao/launchPage.html?channel=。线索正常注册且有渠道标识；\n2、对连接进行代码埋点统计', 2, '2020-06-16', NULL, '2020-06-18', '2020-06-19', '2020-06-15 16:50:35', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (266, 2020, 6, '研发部', 'java', 1413, '黄磊', '教师app banner更换', '1.更换教师 app banner 1的图片-“老师&老板两不误，工资提成全兼顾”，链接不变。\n2.见附件', 2, '2020-06-15', NULL, '2020-06-16', '2020-06-16', '2020-06-15 16:52:57', 1, 3, NULL, NULL);
