INSERT INTO `crm_request_manage` VALUES (268, 2020, 7, '市场', 'H5+Java后台', 453, '毛家俊', '【暂且不做】支付宝小程序', '【该功能暂且不做】\n原任务H5-208中遗留课程购买功能，单独建一个任务。任务内容详见H5-208。\n功能点：\n1、优质课程列表并支持购课', 0, '2020-06-15', NULL, '2020-07-31', '2020-07-31', '2020-06-15 17:14:40', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (269, 2020, 6, '研发部', 'java', 1413, '黄磊', '试听课做限制', '1.试听课可正常结束必须满足以下三个条件（其他任何情况都属于异常，走申请试听补贴流程）：\n①必须老师在教师内上传了课件\n②老师和学生必须同时在教室\n③点击了开始上课', 0, '2020-06-15', NULL, '2020-06-16', '2020-06-16', '2020-06-15 19:52:37', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (270, 2020, 6, '研发部', 'JAVA客户端', 1204, '杨苗苗', '客户端试听课完成课程逻辑优化【紧急】', '试听课申请跳票补贴相关（所有状态都是学生未进入教室，学生只要进入教室，就没有申请跳票补贴）：\n1.课程已取消，未申请跳票补贴：\n老师点击评价结束课程时，文案提示“该课程已取消，无法评价，请联系专属教务”\n2.课程未取消，老师已申请申请跳票补贴\n跳转到评价并结束课程页时，点击评价并结束课程按钮时，文案提示：“该课程已提交试听跳票申请补贴，无法完成课程”\n3.课程未取消，学生未进入教室，老师未提交申请跳票补贴：\n不能直接评价结束该课程，点击评价完成课程按钮时，文案提示：“系统检测到学生未进入教室，请上传课件后，前往APP申请试听跳票补贴”\n提示：申请跳票补贴时，课程状态会自动变为【已取消】\n4.学生中途进入过教室，老师在APP端申请跳票补贴时点击跳票提交按钮，文案提示：系统检测到学生进入过教室，无法申请试听跳票补贴”', 2, '2020-06-16', NULL, '2020-06-16', '2020-06-16', '2020-06-16 10:20:31', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (271, 2020, 6, '市场部', 'H5', 453, '毛家俊', '6月官网页面促销页面', '需求描述: \n①官网https://www.fudaojun.com/promotion.html，banner图、购课专享礼包图片替换；\n②移动端banner图、购课专享礼包图片替换；\n③倒计时更改为截止到6月30号；', 2, '2020-06-17', NULL, '2020-06-18', '2020-06-18', '2020-06-16 11:07:10', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (272, 2020, 6, '研发部', 'JAVA', 1204, '杨苗苗', 'PC端正式课-异常课程-课程被取消逻辑优化', '正式课-异常课程（以下取消状态为CRM操作的取消课程）\n1.教室内，未点击开始上课前，课程已被取消\n点击开始上课按钮，文案提示“该课程已取消，请联系专属教务”\n2.教室内，已点击开始上课，学生未进入过教室，老师已上传课件，点击开始上课15分钟后，该课程被取消，老师点击下课或退出教室，不弹异常课程申请弹窗，出文案提示：“该课程已取消，请联系专属教务”\n3.教师内，已点击开始上课，学生未进入过教室，老师已上传课件，点击开始上课15分钟前，该课程被取消，老师可直接点击下课按钮或退出教室，且文案提示”该课程已取消，请联系专属教务“，不弹评价课程页，课程状态自动变为已取消\n4.老版本，只要是取消状态的课程，不弹异常课程申请补贴弹窗\n5.无论学生是否进入过教师，只要是已取消课程，老师点击评价并结束课程按钮时，文案提示：”该课程已取消，请联系专属教务“\n提示：提交了异常课程申请补贴的课程，课程状态是【学生未进入教室】', 2, '2020-06-16', NULL, '2020-06-23', '2020-06-25', '2020-06-16 11:07:22', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (273, 2020, 6, 'SOP管理中心', 'CRM', 1204, '杨苗苗', '沉睡学员流转功能上线', '\n需求描述: 年初设计的销售部与SOP管理中心两个部门之间制定的数据流转功能重新上线', 2, '2020-06-10', NULL, '2020-06-11', '2020-06-11', '2020-06-16 11:10:53', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (274, 2020, 6, '研发中心', 'CRM', 1204, '杨苗苗', 'CRM二次登录规则优化', '规则是：\n1. 优先使用记录的设备登录（最近两次）。\n2. 如果设备不满足，则使用上次登录IP判定。\n3. 如果上次登录IP不满足条件，则触发二次验证码登录规则。', 2, '2020-06-16', NULL, '2020-06-16', '2020-06-18', '2020-06-16 14:45:41', 1, 3, NULL, NULL);
