INSERT INTO `crm_request_manage` VALUES (278, 2020, 6, '研发部', 'H5客户端', 1413, '黄磊', '422  432版本每次打开客户端进行弹窗更新提醒', 'pc 客户端  4.2.2 和 4.3.2 版本只要检测到有新版本，每次打开客户端都弹出弹窗进行提示：\n①提示到更新最新版本即可\n②一个按钮：立即更新，点击【立即更新】更新到最新版本，右上角可以关闭掉更新弹窗\n③mac端跳转外部更新，win端在内部更新', 2, '2020-06-17', NULL, '2020-06-17', '2020-06-18', '2020-06-17 09:51:28', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (279, 2020, 6, '研发部', '学生app pad pad视频版', 1413, '黄磊', '学生app pad pad视频版进入教室逻辑优化', '1.改动产品：学生app pad pad视频版，包括安卓和ios\n2.原进入教室流程为：【连接白板-连接语音-进入教室】修改为：【连接白板-进入教室-连接语音】，进入教室后若一直连接显示连接语音中或失败，则给老师在右侧推送系统消息：“学生连接语音失败，老师可尝试切换不同上课声道”', 2, '2020-06-17', NULL, '2020-06-18', '2020-06-24', '2020-06-17 11:16:57', 1, 3, NULL, NULL);
