INSERT INTO `crm_request_manage` VALUES (286, 2020, 8, '研发部', '学生app pad  pad视频版', 1413, '黄磊', '教室内使用新课件', '在教室内使用新课件（前置条件-老师使用新版本客户端-支持在线制作课件的功能）：\n①老师使用新版本客户端（win、mac），学生也使用新版本时（客户端、app、pad），使用新课件\n②老师使用新版本客户端（win、mac），学生使用旧版本时（客户端、app、pad），使用新课件需要兼容老版本', 2, '2020-06-18', NULL, '2020-06-18', '2020-08-14', '2020-06-18 20:01:49', 1, 3, NULL, NULL);
