INSERT INTO `crm_request_manage` VALUES (295, 2020, 6, '研发部', 'CRM', 1204, '杨苗苗', '企微微信与CRM自动绑定', '1.家长、老师给顾问企微回复手机号码 ，老师（可以回复其老师id） 时，微信号进行自动绑定crm信息\n2.绑定信息时自动自动生成默认备注，\n老师默认备注名：学科老师（默认第一个学科）-学段-姓名\n例：英语老师-初高中-张爽\n学生默认备注名：客户姓名-CRMID\n例：客户张小二-CRMID123456\n', 2, '2020-06-21', NULL, '2020-06-22', '2020-06-22', '2020-06-21 11:02:47', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (296, 2020, 6, '研发部', 'CRM', 1204, '杨苗苗', '企微微信备注名同步问题', '企微备注名同步问题\n1.我们自己开发的客户详情设置了备注名之后，企微联系人中的备注名自动展示客户详情中设置的备注名\n2.企微本身设置了备注名后，我们自己开发的客户详情同步企微设置的备注名', 1, '2020-06-21', NULL, '2020-06-22', '2020-06-22', '2020-06-21 11:07:16', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (297, 2020, 6, '研发部', 'H5客户端', 1413, '黄磊', '上传课件增加提示', '上传课件增加提示：1.我的课件页面   2.教室内上传课件弹窗页面，见附件\n提示文案：“温馨提示：ppt、pptx课件如果长时间没有显示出来，请把课件转成pdf格式再上传”', 2, '2020-06-22', '2020-06-22', '2020-06-21', '2020-06-22', '2020-06-22 10:02:42', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (298, 2020, 6, '花信', '花信APP', 453, '毛家俊', '线上问题优化', 'bug问题：\n1、安卓分页：商品列表安卓端无分页功能；\n2、安卓：收支明细页面，滑动必定闪退；\n\n优化点：\n1、资产管理文案更改：\n自营收入更改为：自营商品收入\n团队管理佣金更改为：团队管理奖金\n账户余额（可提现）更改为：账户可提现余额\n2、我的团队：仅展示用户名，不展示用户ID\n\n\n\n', 2, '2020-06-22', NULL, '2020-06-23', '2020-06-23', '2020-06-22 10:25:31', 1, 1, NULL, NULL);
