INSERT INTO `crm_request_manage` VALUES (351, 2020, 7, '研发部', 'CRM', 72, '谭杰', 'CRM系统功能优化', '1. 增加登录失败的日志记录。\n2. 增加bug系数数据下载。和开发人员在本月的任务下载。\n3. 每天18点，增加一个已完成任务但是没有填耗时的钉钉通知给开发人员。例如：XXX你当前还有N个任务已完成，但是没有添加实际耗时，请登录CRM查看。\n4. 每天早上10点，增加一个没有填充任务预估耗时的订单通知给开发人员。例如：XXX你当前还有N个任务没有添加预估耗时，请登录CRM查看。', 2, '2020-07-02', NULL, '2020-07-13', '2020-07-14', '2020-07-02 14:44:27', 1, 3, NULL, NULL);
