INSERT INTO `crm_request_manage` VALUES (353, 2020, 8, '师资部', 'CRM', 1204, '杨苗苗', 'crm  老师主页', '\n需求描述: 1.crm板块，老师-辅导学科-学段  标签进行细化。将原来的“小学”“初中”“高中”细化为“1-3年级”“4-6年级”“初一初二”“初三”“高一高二”“高三”（支持多选）同步老师主页  2.老师-辅导学科-学段 下方，新增“阶段”，内容为“基础”“拔高”“竞赛”支持多选\n期望结果: 老师匹配更佳精准', 0, '2020-08-04', NULL, '2020-08-06', '2020-08-13', '2020-07-02 14:50:27', 0, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (354, 2020, 8, '师资运营', 'CRM', 1204, '杨苗苗', 'CRM-老师主页-业务指标统计规则修改', '入口：老师主页-业务指标\n需求：业务指标中的\n试听次数（新/扩）：试听次数改为仅统计试听完成且试听成功的数量，由于学生、老师跳票引起的试听失败的数据不做统计\n新生成单、扩课成功、更换成功：新生成单率的分母也改为仅统计试听成功且试听成功的数量，由于学生、老师跳票引起的试听失败的数据不做统计\n修改规则后老师跳票导致的试听失败不统计，导致成单率变高等问题，系统不管，老师的原因，教务会做处理', 2, '2020-08-06', NULL, '2020-08-13', '2020-08-13', '2020-07-02 14:50:43', 1, 3, NULL, NULL);
