INSERT INTO `crm_request_manage` VALUES (361, 2020, 7, '研发', 'Java后台', 453, '毛家俊', '后台数据调整', '1、更改模块：1⃣️财务/赠送课时审核   2⃣️报表/赠送课时\n数据调整同步：当前活动数据在存储中与原数据存放不一致，需将用户id更改为用户持有人。并用户主动发起申请的备注，在备注前新增文案：用户主动参与活动申请赠送。\n\n2、活动申请数据记录更改：已有活动：公众号分享得课时（上传截图审核）、公众号抽奖、小程序转介绍增课\n更改为：当用户为自己发起申请时，申请人栏中记录发起申请时的持有人信息。在备注前新增“用户主动参与活动申请赠送。”\n', 2, '2020-07-03', NULL, '2020-07-03', '2020-07-03', '2020-07-03 14:55:59', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (362, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', '老师检索页（列表）-教师资格证/合格证：黄标绿标V显示，逻辑优化', '目前，有些老师存在教师资格证，但是因为没有去添加编号，导致qualification_type字段一直为null；所以处理方法：按照老师详情页该图标展示的逻辑： teacher_qualification_card 如果有值，但是qualification_type为null，需要默认该老师有教师资格证，从而在检索页面的列表中，展示黄V\n问题数据查询：SELECT * FROM fudaojun_teacher.`teacher` WHERE state = 1 AND teacher_qualification_card IS NOT NULL AND teacher_qualification_grade IS NOT NULL \nAND teacher_qualification_subject IS NOT NULL AND qualification_type IS NULL', 2, '2020-07-03', NULL, '2020-07-28', '2020-07-30', '2020-07-03 15:46:30', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (363, 2020, 8, '研发部', 'CRM', 1204, '杨苗苗', '独立的代理商系统', '代理商从CRM系统中剥离出来，需要剥离的页面：\n- 用户模块\n- 登录功能-单独\n- 个人资料相关接口\n- 老师模块\n  - 老师检索\n  - 老师详情\n- 顾问模块\n  - 我的联系人\n   - 跟进线索列表\n   - 跟进客户\n   - 试听客户\n   - 待成单客户\n   - 已成单客户\n   - 可续费客户\n - 线索循环池\n - 客户循环池\n - 联系历史\n - 订单列表\n - 我的赠送审核\n - 我的退费用户\n - 更换套餐申请列表\n - 转让列表\n - 我的待匹配课程\n- 我的任务(可以去掉)\n - 监控台(经理)\n- 教务模块\n  - 申请老师绑定\n  - 优秀课程\n- 财务模块\n  - 订单列表\n- 用户池\n - 全局搜索\n - 公海\n - 已分配\n - 线索循环池\n - 线索废弃池\n - 客户循环池\n - 客户废弃池\n- 工单\n - 我的工单\n - IT支持工单\n - 异常工单\n- 市场\n - 开团列表\n- 活动运营\n - 分享海报\n - 礼品兑换\n- 排课\n- 辅导排课\n页面和现在的CRM一样就行，第一期先做代理专员的，代理能用后，区分数据权限', 2, '2020-08-24', '2020-08-25', '2020-09-01', '2020-09-01', '2020-07-03 17:50:52', 1, 3, NULL, NULL);
