INSERT INTO `crm_request_manage` VALUES (414, 2020, 8, '研发', 'java', 1413, '黄磊', '记录刷题错题中正确的题目', '5.后端需要记录 刷题错题中的正确的题目，正确的题目为正确的选择题\n  ①加入错题本（偷看答案+加入错题本）的题目全部算为错误，未加入错题本的题目有错误的选择题列为错误，无法判断对错的题目不算对也不算错误\n  ②每次做的几道题目，题目总数就是几，不管学生有没有全部做完', 2, '2020-07-13', NULL, '2020-07-24', '2020-08-05', '2020-07-13 14:55:24', 1, 3, NULL, NULL);
