INSERT INTO `crm_request_manage` VALUES (422, 2020, 7, '研发', 'crm', 453, '毛家俊', '工单问题处理', '发起工单状态中：赠送课时扣除、正式课扣除、课时转移及更改套餐时，需新增判定，发起申请时该套餐中是否存在已排课课时、正在上课中、课后未评价状态课时（课程未状态）。若存在锁定课时，则提示“请先取消该套餐内的锁定课程，在进行申请更改”。若无锁定课时，则按正常流程发起申请。', 2, '2020-07-14', NULL, '2020-07-20', '2020-07-23', '2020-07-14 21:07:09', 1, 2, NULL, NULL);
