INSERT INTO `crm_request_manage` VALUES (434, 2020, 7, '研发部', 'java', 1413, '黄磊', '进行中的课程优化', '若进行中的正式课在crm点击辅导完成，时间跑完，pc端在教室内点击下课不能改变课程状态 \n①点击下课按钮时进行提示：“该课程已完成，请联系专属教务”，并直接退出到 “已上课程” 界面\n②若时间跑完，未点击下课按钮，此时点击右上角退出教室按钮，不在弹出弹窗，直接退出到 “已上课程” 界面，并进行toast提示：“该课程已完成，请联系专属教务”', 2, '2020-07-16', NULL, '2020-07-20', '2020-07-24', '2020-07-16 18:10:34', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (435, 2020, 8, '研发部', 'IOS  H5', 479, '王梢宇', 'ios部分控件更换', '1.app store 所有app，UIWebView 控件 替换成 WKWebView  控件\napp store 中ios APP所用到控件的页面为(需要查看是否显示正常)：\n注册协议 \n购买协议（课程商城-点击支付） \n我的优惠券\n', 2, '2020-07-15', NULL, '2020-07-15', '2020-08-13', '2020-07-16 18:12:38', 1, 2, NULL, NULL);
