INSERT INTO `crm_request_manage` VALUES (442, 2020, 7, '市场', 'iOS', 453, '毛家俊', '马甲包内容优化', '作业辅导内容优化：\n1、猜你想看视频内容新增辅导君app中的展示视频，按顺序展示浙江卫视、企业宣传片、产品宣传片；\n2、老师展示头像替换\n3、4个菜单中，单词翻译更改为“公开课”，点击跳转公开课页面内容（一对一家教的页面及内容）。直击高考更改为“我的课程”，点击跳转“我的课程页面”。轻松学古文模版不变，未登录时点击跳转登录页面，已登录时点击文案显示为“该内容正在努力补充，请稍后在来”。', 2, '2020-07-20', NULL, '2020-07-22', '2020-07-23', '2020-07-20 12:02:18', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (443, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-学生主页-课时工单显示优化', '入口：学生主页-课时工单\n需求：所有的课时变化都加上单位，是小时就显示小时，分钟就显示分钟', 2, '2020-07-20', NULL, '2020-07-21', '2020-07-23', '2020-07-20 15:09:58', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (444, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-学生主页发起工单、工单审核优化', '一：入口：学生主页-申请-发起工单\n1.类型为【异常课程确认处理】希望解决方式格式改为扣除时长从（）改为（）\n时长还是支持选择或输入\n2.类型为【299套餐回馈】课时数加上单位\n是小时就展示小时，分钟就展示分钟\n二、入口：工单-工单审核\n1、类型为【异常课程确认处理】下的工单\n待审核、已拒绝、待处理、处理完成、客服已拒绝tab列表下的【异常课程确认处理】工单\n希望解决方式字段下时长格式改为：扣除时长从（）改为（）\n例：扣除时长从2小时改为1小时\n2.类型为【299套餐回馈】下的工单\n待处理、处理完成、客服已拒绝tab列表下的【299套餐回馈】工单\n期望解决方式字段下的赠送课时加上单位，是小时就显示小时，分钟就显示分钟\n详情见附件原型', 2, '2020-07-20', NULL, '2020-07-28', '2020-07-23', '2020-07-20 15:15:06', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (445, 2020, 10, '市场', 'h5', 453, '毛家俊', '官网改版', '需求背景：原官网许多内容因违反广告法被删除，现需根据当前的业务状态设计一版新的官网页面，上线时先使用新的地址进行测试投放状态。', 2, '2020-09-01', '2020-09-05', '2020-10-19', '2020-10-20', '2020-07-20 15:52:50', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (446, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-老师主页新增重新绑定功能【紧急】', '入口：老师主页\n一 新增【重新绑定】功能，仅限之前有过绑定关系的老师和学生，不受30天再次绑定限制，可随时重新绑定\n操作权限：1.有品控绑定关系的教务可以操作（只要是品控教务就可以操作）\n2.辅导君权限组\n二 重新绑定弹窗中的重新绑定原因为必填\n绑定成功出绑定成功toast\n详情见附件原型', 2, '2020-07-20', NULL, '2020-07-22', '2020-07-23', '2020-07-20 16:40:22', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (447, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-学生主页 老师主页解绑提示文案优化', '1.学生主页解除老师绑定时，30天文案提示字号改为20，加粗展示\n2.老师主页解除绑定是，30天文案提示字号改为20，加粗展示\n详情见附件图', 2, '2020-07-20', NULL, '2020-07-27', '2020-07-23', '2020-07-20 16:44:30', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (448, 2020, 7, '花信app', '花信app', 453, '毛家俊', '商品增加二三级分类', '添加商品可选二三级分类选项，二三级分类模块已经完成（自营商品添加功能）\n以下为添加自营商品至分销商城规则\n一、分销商城展示规则：\n当自营商品选择了多个1级分类时，分销商城中默认展示该商品的归属于的1级分类中（若有多个1级分类，选中了1个上架后在则其他1级分类无法再次选择，在分销商品中仅有一个商品记录信息）\n\n二、分销商城上架规则：\n当添加分销商品上架销售时，每次添加上架需要店主选择商品的分类，选择完成后在自营店铺分类中展示。\n\n新增备注：\n原一款商品仅限在一个分类中展示，现更改为1款商品支持在多个分类（包括1/2/3级）中展示。\n\n一个款商品最多勾选3个分类。支持选择不同类型的分类。选中后会在1级分类中显示该类别下选中的几个分类。\n\n当勾选第四个分类时提示：“最多勾选3个分类哦”', 2, '2020-07-21', '2020-07-21', '2020-07-23', '2020-07-23', '2020-07-21 09:50:42', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (449, 2020, 7, '研发部', 'CRM', 1204, '杨苗苗', 'CRM-代理商-代理商报表优化', '现状：代理商-代理商报表中仅显示了已经分配给代理的数据，在公海里面的属于代理商的数据没有展示\n需求：公海中）的代理商线索（不管有没有分配给代理专员也展示在代理商报表中，没有持有人和初始分配人时直接显示“-”就行，\n没有持有人的数据也支持下载', 2, '2020-07-21', NULL, '2020-07-24', '2020-07-24', '2020-07-21 11:54:41', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (450, 2020, 7, '研发部', 'JAVA', 1204, '杨苗苗', 'PC客户端弹窗二维码修改', '现状：有正式课的老师，登录PC客户端时，弹出对应专属教务的企微二维码弹窗，弹窗可手动关闭\n需求：弹出的企微二维码变为固定的二维码，不弹对应教务的企微，具体二维码见附件', 2, '2020-07-21', NULL, '2020-07-22', '2020-07-22', '2020-07-21 14:29:37', 1, 2, NULL, NULL);
