INSERT INTO `crm_request_manage` VALUES (458, 2020, 7, '花信', 'java后台', 453, '毛家俊', '商品展示状态优化', '原：分销商城里面的商品，店主下架一次后，就变成了历史分销。如果重新去选择上架，那就是生成一条新的商品信息，和原先的没有联系。\n更改为：同一个分销商品下架后，再次上架，仍为同一个商品信息。再次点击原分销商品的链接后，仍可以打开商品详情。', 2, '2020-07-22', NULL, '2020-07-23', '2020-07-23', '2020-07-22 18:32:13', 1, 2, NULL, NULL);
