INSERT INTO `crm_request_manage` VALUES (465, 2020, 7, '研发部', 'QT', 1413, '黄磊', '渲染引擎选择', 'windows客户端 右上角-高级设置-渲染引擎选择：\n1.老用户：（安装过辅导君客户端版本的）默认选择OpenGL选项\n2.新用户：默认选择OpenGL选项，OpenGL有问题时默认“软件模拟”\n3.若用户上次设置过哪个选项，再次安装时就是哪个选项', 2, '2020-07-23', NULL, '2020-07-23', '2020-07-24', '2020-07-24 16:03:41', 1, 2, NULL, NULL);
