INSERT INTO `crm_request_manage` VALUES (498, 2020, 8, '市场部', 'h5+java后台', 453, '毛家俊', '服务号—活动内容更新（8月）', '需求描述: 抽奖活动8月内容更新：\n1、抽奖活动页面展示及活动规则更改：\n主题：8月购课赢好礼    100%中奖\n副标题：暑假要逆袭，好课第一位\n活动规则：规则内容见附件\n\n2、抽奖活动奖品更换，并修改中奖概率 （内容见附录）\n\n3、分享得课时文案更换8月最新文案\n暑假黄金期，名师1对1，\n安心在家学，逆袭新学期。\n送你一节诊断课，小初高全科都有，扫码添加辅导老师领取，还会帮你定制学习计划喔', 2, '2020-08-04', '2020-08-04', '2020-08-05', '2020-08-05', '2020-08-04 10:30:19', 1, 3, NULL, NULL);
