INSERT INTO `crm_request_manage` VALUES (521, 2020, 8, '教学中心', 'CRM', 1204, '杨苗苗', 'CRM-学生主页-发起试听类型和时间修改', '入口：学生主页-发起试听\n1.试听类型新增【代课】\n其他选项、字段和扩课一样\n逻辑也和扩课一样\n2.上课时间由时间点改为时间段\n点开之后，变为选择开始时间，选择结束时间\n时间都是整点 不可以跨天选择\n详细内容见附件', 2, '2020-08-14', NULL, '2020-08-24', '2020-08-27', '2020-08-14 10:01:12', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (522, 2020, 8, '财务部', 'CRM', 1204, '杨苗苗', '退费审核挽单失败修改和退费列表数据优化', '一、入口：退费审核-退费详情页-挽单失败\n退费处理\n挽单失败时，【银行开户行】输入时，出现对应银行全称和联行号，仅支持下拉选择的银行，联行号见附件表格\n二、入口：财务-退费申请审核\n列表展示的和下载的表格中字段新增【收款方联行号】\n【开户省市】拆分为【开户省】【开户市\n其他字段不变\n详情见附件\n', 2, '2020-08-15', NULL, '2020-08-18', '2020-08-26', '2020-08-15 18:38:57', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (523, 2020, 8, '研发部', '企微+CRM+H5', 1204, '杨苗苗', '企微自动建群', '1.获取剩余课时大于等于10，学生消费金额大于2000的活跃企微未建群的学生信息，学生绑定的老师中已绑定微信的老师数据，已绑定CRM的老师的微信持有人信息，具体字段：学生CRMid， 学生姓名，持有人姓名，持有人组，持有人大区，学生年级，老师id，老师姓名，老师持有人\n2.老师微信的持有人只要何宇明、杨苗苗、毛家俊的，其他人的不用拉取，数据传给前端，自动建群\n3.自动建群后的群名称为辅导君+学生姓名+学生年级+学习群，例：辅导君谭杰高三学习群\n4.学生已加群，不管是不是当前持有人拉进群的，都不生成企微建群记录\n5.学生未加群，顾问离职，不管新的持有人在不在群里，都生成新的企微建群记录\n', 2, '2020-08-17', NULL, '2020-08-19', '2020-08-27', '2020-08-17 10:44:42', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (524, 2020, 8, '研发部', 'CRM', 1204, '杨苗苗', '兼职试听课数据统计维度和图表修改', '入口：老师画像-兼职老师数据统计-兼职试听课数据\n1.不同辅导学段科目下， 统计已绑定学生数小于等于10(包含0)有无教资的兼职老师改为不同辅导学段科目下， 统计已绑定学生数小于等于10(包含0)有无教资的活跃兼职老师\n在原来的统计基础上只统计活跃老师就行，小问号的统计说明也要修改\n2.图表中有教资、合格证、无教资合并为一个柱状，试听量换个和有教资老师不一样的颜色，放置顺序和原型中一致，详情见附件\n', 2, '2020-08-17', NULL, '2020-08-19', '2020-08-20', '2020-08-17 11:41:48', 1, 3, NULL, NULL);
