INSERT INTO `crm_request_manage` VALUES (546, 2020, 8, '研发', 'java后台', 453, '毛家俊', '小程序用户注册排查', '在拼团过程中发现一例线索1，其注册手机号是用户2的家长号码，需排查该线索是如何创建，并避免出现此种情况。该问题是在fudaojun库中，crm中数据正常。\n（正常数据应该为已关联的号码都无法注册为线索）', 2, '2020-08-21', NULL, '2020-08-25', '2020-08-26', '2020-08-21 10:58:08', 1, 3, NULL, NULL);
