INSERT INTO `crm_request_manage` VALUES (549, 2020, 9, '市场部', NULL, NULL, NULL, '服务号-分享得课时', '\n需求描述: 分享海报中新用户扫码注册页面优化\n1、优化目的\n当前的好友注册页面中的文案还是“截图分享赢好礼”为主题的相关内容，对促进用户转化不是很友好\n2、建议优化方案\n分享海报中好友扫码注册页面内容重新优化设计', 10, NULL, NULL, NULL, NULL, '2020-08-25 14:20:43', 0, NULL, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (550, 2020, 8, '教学中心', 'H5 java', 1413, '黄磊', '试听课老师端增加消息提醒', '试听课老师端在右侧消息栏增加消息提醒，具体规则见原型\n', 2, '2020-08-26', NULL, '2020-08-27', '2020-08-27', '2020-08-26 10:19:26', 1, 2, NULL, NULL);
