INSERT INTO `crm_request_manage` VALUES (567, 2020, 9, 'IT支持', '学生app ', 1413, '黄磊', '学生使用移动端设备上课时记录主动操作事件', '学生在使用手机或者pad上课时，记录主动操作事件，并给老师端进行消息提示：\n事件类型：1. 后台运行程序   2.主动退出教室   3.结束进程，退出教室（具体看开发能否实现，无法记录就不做）\n记录：在课程详情日志版块增加“主动操作日志（学生）”，记录学生主动操作的事件\n改动产品：app  pad  pad视频版\n具体见原型\n', 2, '2020-09-01', NULL, '2020-09-08', '2020-09-15', '2020-09-01 11:17:21', 1, 3, NULL, NULL);
