INSERT INTO `crm_request_manage` VALUES (569, 2020, 9, '教学中心', '老师app', 1413, '黄磊', 'app消息推送增加推送类型', '1.老师端：（这里的老师只在平台通过审核，正式签约的老师，弃用老师不进行提醒）\n①增加更新空闲时间提醒，提示内容：“老师你好，为了高效匹配接单，请及时更新空闲时间和接课状态”\n 提示频率：相隔14天提醒一次（app不管打不打开都要进行消息推送）比如2020-04-07进行了提示，等到2020-04-21再次进行提醒，点击消息跳转到老师app【接单设置】页面，第一次提醒时间为后台发布后第三天进行提示。\n\n②老师在申请接单后进行提醒（需要和CRM配合）：\n接单成功（通过教务匹配已经和申请的该老师排上了课），提醒内容：老师您好，您申请的***同学的试听单已通过，请立即查看详情，合理安排上课内容。点击消息推送跳转到【接单页面】，状态是申请成功\n\n', 2, '2020-09-01', NULL, '2020-09-02', '2020-09-17', '2020-09-01 17:23:20', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (570, 2020, 9, '教学中心', '老师APP', 1413, '黄磊', '老师app【我的协议】增加限制', '老师app【我的协议】增加限制：\n1.全职老师不管什么状态一直不显示【我的协议】（目前未限制，全职老师走的是线下纸质签约，没有这个版块）\n2.兼职：当前老师是弃用老师时，app 我的里面不显示【我的协议】\n3.兼职：当前老师是试讲未审核通过时，app 我的里面不显示【我的协议】，当试讲审核通过后，才显示该板块，可以进行签约', 2, '2020-09-02', NULL, '2020-09-08', '2020-09-17', '2020-09-01 18:25:12', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (571, 2020, 9, '教学中心', '老师app', 1413, '黄磊', '老师可以在app设置标签', '老师可以在app设置标签，设置的标签同步到crm-老师个人主页，在个人主页教务有权限修改标签，修改的内容同步到app显示\n位置：老师app-我的-我的信息-【我的标签】，页面流程及规则见原型', 2, '2020-09-04', '2020-09-04', '2020-09-08', '2020-09-17', '2020-09-01 19:17:44', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (572, 2020, 11, '研发部', 'pc客户端 ', 1413, '黄磊', '教室内左侧工具栏布局优化', '教室内左侧工具栏布局优化，包括老师端和学生端，详情见原型（更新到 456 461 462  ）\n1.改动：将线条和形状分开，线条分为：直线工具  虚线工具   直线箭头（新增的）\n形状分为：矩形工具  等腰三角形  直角三角形   圆形工具   正圆工具  \n新增直角三角形、等腰三角形，默认展示直线工具\n2.新增：直角坐标系\n3.默认展示三种颜色\n4.新增一个圆形工具，新圆逻辑：需要以圆心和半径为参照来画圆，画笔点击的位置为圆心\n ①老师和学生都是新版本：旧的圆和新的圆都展示\n ②老师新版本,学生旧版本：展示两个圆形工具（新的和旧的）\n5.老师新版本，学生旧版本这种情况下：老师端新功能置灰，点击时给提示：对方不是最新版本，请提醒更新到最新版本后再次使用', 2, '2020-10-19', '2020-11-05', '2020-11-09', '2020-11-10', '2020-09-01 19:19:25', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (573, 2020, 10, '研发中心', 'CRM', 72, '谭杰', '百川系统 - 用户相关接口第一版', '1. 个人信息接口获取。记得增加缓存，参考  /auth/my/profile 接口\n2. 修改密码接口。\n3. 修改个人资料接口。\n4. 修改用户状态，休假，在职 切换。\n5.用户搜索接口。', 2, '2020-09-02', NULL, '2020-10-31', '2020-10-31', '2020-09-02 10:36:31', 1, 3, NULL, NULL);
