INSERT INTO `crm_request_manage` VALUES (575, 2020, 9, '研发中心', 'CRM', 1204, '杨苗苗', '企业微信 - 问题机器人 - 问题追踪功能', '1. 在消息发送之后，增加一段文案提示：”您的问题是否得到解决？若没有解决，请准确描述问题，例：家长不知道怎么在官网下载辅导君，准确描述为：客户端下载或windows客户端下载“\n2. 记录用户的问题是否被解决。\n3. 没有被解决则通知产品经理的企业微信。', 2, '2020-09-02', NULL, '2020-09-04', '2020-09-07', '2020-09-02 14:16:19', 1, 3, NULL, NULL);
