INSERT INTO `crm_request_manage` VALUES (657, 2020, 10, '市场', 'CRM', 453, '毛家俊', '运营-活动数据统计优化', '1、现统计报表中统计的数据维度为渠道名称状态为“开启”中，需更改为状态为“开启+关闭”，展示所有状态的渠道统计。\n2、数据展示：线索量、试听量、成单量、参与人数、完成人数、老线索成单量、获客成本按四舍五入取整展示，下载的数据报表数据不变。', 2, '2020-09-27', NULL, '2020-10-14', '2020-10-15', '2020-09-27 10:18:30', 1, 3, NULL, NULL);
