INSERT INTO `crm_request_manage` VALUES (669, 2020, 10, '师资部', 'JAVA', 1204, '杨苗苗', 'CRM-全职老师等级规则调整', '\n需求描述: CRM老师主页，教师等级模块，全职老师若当月课耗不满足10小时，会自动降级，该功能希望取消，对全职老师不做由于课时不满足就降级的限制', 2, '2020-10-27', NULL, '2020-10-28', '2020-10-29', '2020-09-28 11:56:13', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (670, 2020, 10, '教学中心', 'CRM', 1204, '杨苗苗', '报表-报表下载-新增更换老师数据下载下载', '入口：报表-报表下载\n新增更换老师数据下载\n数据来源：【教务-更换老师列表-未处理&已处理数据】\n按发起时间下载\n具体下载字段：\n状态：未处理、已处理\n学生姓名、学生ID、年级、科目、发起人、发起时间\n匹配老师（新）姓名，匹配老师（新）ID、原老师姓名、原老师ID、辅导学生数（原）所属教务（原）、更换原因、操作记录、处理结果\n没有数据的字段下载时直接为空\n详情见附件\n', 2, '2020-10-09', NULL, '2020-10-21', '2020-10-22', '2020-09-28 15:11:56', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (671, 2020, 9, '市场', 'Java后台', 453, '毛家俊', '推送内容更新', '分享得课时文案更改（30号更新）：\n孩子开学一个多月了，月考、期中考一点不能松懈。\n还好有辅导君老师的在线1对1课，学习有方法。\n送你一节试听课，小初高全科都有，扫码领取。\n', 2, '2020-09-28', NULL, '2020-09-30', '2020-09-30', '2020-09-28 15:58:02', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (672, 2020, 9, '研发', 'c r m', 453, '毛家俊', '礼品兑换优化', '活动运营-礼品兑换模块，下载发货单时，当礼品地址URL超过255字符时，该用户的收件地址不会加载到表格中。\n现需对该情况进行优化：若出现下载过程中，礼品地址URL超过255字符，则默认下载250字符，超出部分不下载。', 2, '2020-09-29', NULL, '2020-09-29', '2020-09-30', '2020-09-29 14:40:59', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (673, 2020, 10, '研发部', 'h5', 1413, '黄磊', '课件编辑器再次调整', '1.增加了ctrl+s 、Command+s保存的快捷键，点击时出现进度条，保存过后不会退出\n2.左上角增加了撤销、反撤销的功能，快捷键win：ctrl+z，ctrl+y，Command+z，Command+y：\n  进行操作后才有撤销功能，没有进行操作时，两个按钮都是置灰状态，只有进行撤销后，才有反撤销的操作，撤销过几次就可以反撤销几步\n  最多撤销10步的操作，撤销只针对单页的，若此时该页面可以撤销，有切换了其他页面，再次回到该页面时，两个按钮置灰', 2, '2020-09-29', NULL, '2020-09-30', '2020-10-10', '2020-09-30 09:40:25', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (674, 2020, 10, '研发中心', 'CRM', 1204, '杨苗苗', 'CRM-数据分析-Top10员工分析新线索跟进达标率、线索总量与单量占比图表坐标轴合并', '入口：数据分析-Top10员工分析\n需求：1.新线索跟进达标率\n现在每个图表的坐标轴都是单独的，把所有的数据放到一个图表里面，用同一个坐标轴\n2.线索总量与单量占比\n改为共用一个坐标轴，放到同一个横坐标下面\n详情见附件', 2, '2020-09-30', NULL, '2020-10-12', '2020-10-15', '2020-09-30 10:35:20', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (675, 2020, 10, '研发部', '老师app', 1413, '黄磊', '签约规则调整', '1.老师app（ios+安卓），进入我的-我的协议页面：\n\n①提前续约时（当前时间≤到期时间），签订的时间是“到期时间”+“续约时间”\n②过期后，（当前时间＞到期时间），签订的时间是“当前时间”+“续约时间”，过期后续约按钮还需要继续显示（目前安卓是显示的，ios需要改），不再进行提示签约提示\n③默认进入该页面时，显示的时间段（见附件）也要根据相应规则显示\n', 2, '2020-10-07', NULL, '2020-10-19', '2020-10-21', '2020-09-30 16:05:56', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (676, 2020, 10, '研发中心', 'CRM', 72, '谭杰', 'CRM-试听匹配功能限制移除', '1. 移除学生主页的老师绑定的试听绑定的30天重复绑定限制。\n2. 增加试听绑定老师只能绑定试听课在90天内结束的课程。', 2, '2020-10-01', NULL, '2020-10-01', '2020-10-01', '2020-10-01 16:29:49', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (677, 2020, 10, '客服中心', 'CRM', 1204, '杨苗苗', '满意度回访填写新增老师反馈', '入口：学生主页-添加客户满意度\n1.填写客户满意度回放的时候，是否存在问题老师选择是的时候\n2.出现老师反馈弹窗，弹窗顶部添加【每次只能选择一位老师反馈，可多次提交】文案提示，加粗标红展示\n3.【学生绑定的老师数据】该学生绑定的所有老师数据，包含已经解绑的老师，老师列表字段：老师姓名、ID、学科及学段\n4.【反馈内容】支持多选，和【老师检索-反馈-课堂外】反馈的内容不同点：删除了【讲解能力差】【与学生私下交往过密】选项，新增 【频繁调整上课时间】【老师不熟悉当地教材和教学重点】【上课环境嘈杂】【老师无法满足学生视频上课的需求】【老师不专业，缺乏教学经验】【群消息不回复】选项\n5.具体描述、希望处理结果、上传图片功能不变，上传图片改为非必填项，其他都是必填项\n6.点击确定自动生成【课堂外】老师投诉，数据自动同步到教务-投诉处理列表页，一次只能选择一位老师，可多次提交\n详情见附件原型\n\n\n\n\n', 2, '2020-10-12', NULL, '2020-10-19', '2020-10-22', '2020-10-09 09:35:06', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (678, 2020, 10, '业务中心', 'CRM', 1204, '杨苗苗', 'CRM-报表-销售数据区报表', '\n需求描述: 增加一个实际出席人数和一个出席率：实际出席人数根据出席时间计入出席当月，包含当月新线索和老线索的出席，同一线索出席多次记为1个出席人数；出席率=实际出席人数/第一次试听总量   \n期望结果: 能直接看到各个区域的试听课出席情况', 0, '2020-10-14', NULL, '2020-10-19', '2020-10-22', '2020-10-09 09:35:18', 0, 3, NULL, NULL);
