INSERT INTO `crm_request_manage` VALUES (681, 2020, 10, '学管部', 'CRM', 1204, '杨苗苗', 'CRM-学管-我的待匹配课程新增【申请匹配】', '需求：学管发起的所有试听单（包括超额匹配），在学管-我的待匹配列表都能看到，点击进入匹配页面，先选择老师再提交匹配申请\n2.申请时支持上传图片（非必填）\n3.经理收到的钉钉消息通知展示发起信息，显示发起的是试听匹配审核单和对应的学生信息\n详情见附件\n二、支持上传多张截图（非必填）\n比之前的顾问-我的待匹配课程新增的申请匹配功能多一个支持上传多张截图功能\n逻辑和顾问的手动匹配申请逻辑一样，都是先选择老师在提交申请', 2, '2020-10-09', NULL, '2020-10-13', '2020-10-29', '2020-10-09 10:09:30', 1, 3, NULL, NULL);
