INSERT INTO `crm_request_manage` VALUES (708, 2020, 10, '研发部', 'java h5', 1413, '黄磊', '试讲课不可以提异常课程', '试讲课是异常课程时不弹出申请弹窗，包括在教室内的弹窗、课程评价页面的弹窗（包括pc和app）\n只有正式课有这个异常课程申请的弹窗，其他课 试听课   试讲课  小班课   大班课（培训课）都不弹出\n\n时间跑到15分钟时：\n1.点击下课按钮可以下课，不弹出异常申请弹窗，直接跳转到评价页面\n\n当时间跑完时：\n1.点击下课按钮，直接下课并跳转到评价页面（按正常流程走）\n2.点击退出教室按钮时，弹出确定弹窗，弹窗建附件(点击确定后，也跳转到评价页面)', 2, '2020-10-13', NULL, '2020-10-13', '2020-10-15', '2020-10-10 10:10:30', 1, 3, NULL, NULL);
