INSERT INTO `crm_request_manage` VALUES (733, 2020, 10, '研发部', 'h5 客户端', 1413, '黄磊', '教务端-课件制作页面调整', '1.教务端-课件制作页面新增tab切换，分为：精品课件 和  课件模板 ，教务在制作的时候可以选择 制作模板 还是 制作课件，老师端可以在我的课件页面进行使用，具体见原型图：\n  精品课件：展示所有的课件（包括试听课件以及正式课课件）\n  课件模板：展示所有的模板（课件中只有样式，没有具体内容）\n', 2, '2020-10-16', '2020-10-16', '2020-10-20', '2020-10-21', '2020-10-16 09:55:20', 1, 2, NULL, NULL);
