INSERT INTO `crm_request_manage` VALUES (735, 2020, 10, '教学中心', 'CRM', 1204, '杨苗苗', '顾问-我的待匹配课程-手动匹配改为先选择老师再提交申请', '1.手动匹配申请改为先选择老师再提交申请\n2.申请时支持上传图片（非必填）\n3.经理收到的钉钉消息通知展示发起信息，显示发起的是试听匹配审核单和对应的学生信息\n详情见附件', 2, '2020-10-16', NULL, '2020-10-21', '2020-10-22', '2020-10-16 16:40:07', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (736, 2020, 10, '教学中心', 'CRM', 1204, '杨苗苗', '学生主页-直接绑定新增经理钉钉审核', '1.直接绑定现在需要教务审核的，现有的教务审核不动，\n钉钉同步给对应的经理审核，无论是教务还是经理都可以通过或拒绝，\n经理审核后，【教务-老师申请】也会被同步审核完成，\n教务在【教务-老师申请】列表审核之后，经理收到的钉钉审核也会同步审核完成\n2.经理审批后，发起人收到的钉钉通知\n1.显示发起的是试听匹配还是直接绑定申请\n2.收到的通知显示学生ID\n同意时：文案修改为：您发起的直接绑定（学生：黄小可1234）申请已通过，请及时登录CRM查看\n比之前新增一个学生ID\n拒绝时：您发起的直接绑定（学生：黄小可1234）申请被拒绝，拒绝理由为：老师不符合要求，请前往CRM重新发起申请\n比之前新增学生ID，新增拒绝理由\n发起人是代理的，同意和拒绝都以短信形式通知，通知文案和总部的一样\n详情见附件\n\n', 2, '2020-10-16', NULL, '2020-10-28', '2020-10-29', '2020-10-16 16:42:36', 1, 3, NULL, NULL);
