INSERT INTO `crm_request_manage` VALUES (753, 2020, 10, '研发', 'Java后台', 453, '毛家俊', '抽奖/分享得课时赠送课时处理', '一、数据处理：\n原抽奖/分享得课时在赠送课时的时候，对赠送的课时会统计课时单价（若用户有套餐，则取套餐单价，若无套餐则为0）\n需更改为：\n对抽奖/分享得课时的赠送课时的单价进行处理，全部更改为0（课时状态：未上课/已上课）\n\n二、规则处理：\n对抽奖/分享得课时的赠送课时的单价进行处理，课程单价写入是为0.\n', 2, '2020-10-22', NULL, '2020-10-26', '2020-10-27', '2020-10-22 14:59:36', 1, 3, NULL, NULL);
