INSERT INTO `crm_request_manage` VALUES (755, 2020, 10, '投放部', 'CRM', 1204, '杨苗苗', 'CRM数据分析模块开通申请', '需求背景: 数据统计需求未把数据分析-top10员工统计功能利用起来。\n需求描述: 申请给市场专员：翁玲、周城、李爽，开通CRM-数据分析-top10员工统计功能，便于核对数据统计表格精准性。\n期望结果: 专员通过CRM-数据分析-top10员工统计功能分析各区、小组、员工各项数据转化情况，无误后可同步给销售各区经理使用。', 2, '2020-10-23', NULL, '2020-10-23', '2020-10-23', '2020-10-23 10:27:55', 1, 2, NULL, NULL);
