INSERT INTO `crm_request_manage` VALUES (801, 2020, 11, '研发部', 'java ', 1413, '黄磊', '排课限制规则调整', '原规则：有5节或5节以上课程未评价结束，不允许排课\n1.改为：有10节或10节以上课程未评价结束，不允许排课（包括未开始和进行中的课）\n2.不满足要求时，提示改为：您有10节或10节以上课程未评价结束，请先评价结束\n3.包括手机app  和pc端排课\n\n', 2, '2020-11-03', NULL, '2020-11-10', '2020-11-12', '2020-11-03 09:36:43', 1, 3, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (802, 2020, 11, '研发部', 'h5  java crm', 1413, '黄磊', '成绩录入-分数支持一位小数', '学生成绩录入-分数支持一位小数\n1.pc客户端：教室内成绩录入中，分数录入支持两位小数，例：90.50，录入两位小数时，只取一位小数（四舍五入），包括老师端和旁听端\n2.crm—学生个人主页-添加成绩：成绩录入弹窗中 成绩 选项支持一位小数（正数），不满足要求时进行提示：只能输入整数或者一位小数', 2, '2020-11-02', NULL, '2020-11-04', '2020-11-10', '2020-11-03 10:10:13', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (803, 2020, 11, '学管中心', 'CRM', 1204, '杨苗苗', 'CRM-学管-我的学生分类优化', '1、默认的页面是在第一栏“活跃学生”，“所有学生”调整至第一栏默认页面\n目的：默认检索库里的所有学生\n2、活跃学生去除未报课学生（目前也会在活跃学生里面）\n3、新增一列未报课学生，未报课学生定义：69拼团，未成单客户，299团购客户，其他活动进来有课时但未购课的学生\n系统先筛选未报课学生然后在筛选0课时的学员，然后在筛选特殊学员，在筛选沉睡学员和活跃学生\n列表展示顺序为所有学生、活跃学生、未报课学生、沉睡学生、特殊学生、0课时学生\n4、小问号的说明文案改为：\n活跃学生：60天内有正式课课耗学员\n未报课学生：69拼团，未成单客户，299团购客户，其他活动进来有课时但未购课的学生\n沉睡学生：60以上未上课学员\n特殊学生：剩余课时大于等于100小时学员\n0课时学生：剩余课时为0的学员\n活跃学生和0课时学生列表数据不重复\n活跃学生和特殊学生列表数据不重复\n例：A学生60天内有正式课课耗，但课时已为0，A学生只出现在0课时学生列表', 2, '2020-11-05', NULL, '2020-11-10', '2020-11-12', '2020-11-03 10:11:28', 1, 3, NULL, NULL);
