INSERT INTO `crm_request_manage` VALUES (881, 2020, 11, '研发中心', 'crm', 72, '谭杰', '试听/直接绑定 - 经理审核列表功能', '现在有部分经理说有时候收不到通知，导致审核不了。故现在增加一个审核列表（目前仅供查看，不支持直接审核）。\n1. 在菜单【顾问】【监控台下面】新增一个【钉钉审核列表】\n2. 显示的字段，【类型】【申请人】【学生】【老师】【申请时间】【审核状态】【审核内容】.\n3. 审核内容提供复制功能，让他方便在钉钉里面打开审核。', 0, '2020-11-23', NULL, '2020-11-30', '2020-11-30', '2020-11-23 14:28:47', 0, 2, NULL, NULL);
