INSERT INTO `crm_request_manage` VALUES (927, 2020, 12, '花信', '花信项目', 1865, '蒋明', '拉取7月份至今的待发货数据', '需求描述：拉取7月份至今的待发货数据，供花信运营发货\n时间维度：2020年7月1日0点起至2020年11月30日23点59分59秒\n拉取字段：创建时间、订单编号、订单金额、收件人姓名、收件人电话、收货地址、商品名称、商品价格、商品数量\n\n注意：一个订单可能对应多个商品\n', 2, '2020-12-01', NULL, '2020-12-01', '2020-12-01', '2020-12-01 14:00:52', 1, 3, NULL, NULL);
