INSERT INTO `crm_request_manage` VALUES (936, 2020, 12, '运营中心', 'H5开发', 1865, '蒋明', '分享有礼活动', '新增三个菜单及一个领取奖品H5：1、配置分享得课时活动2、审核分享得课时的奖品及课时信息\n活动规则：未报课用户、报课用户都可以参加，且一个月内区分阶段可参加多次，具体以活动配置为主，审核通过后满足条件赠送固定奖品或课时。\n【CRM审核相关原型图】https://org.modao.cc/app/43fe3a82ccce815634636d2573949304d90ecb13?simulator_type=outside_artboard&sticky\n【领取奖品H5】https://org.modao.cc/app/2071e72ea6454808dfdd7659e5a66de07732c467?simulator_type=outside_artboard&sticky', 2, '2020-12-10', '2020-12-14', '2020-12-18', '2020-12-23', '2020-12-01 16:46:01', 1, 3, NULL, NULL);
