INSERT INTO `crm_request_manage` VALUES (978, 2020, 12, '学管', 'crm', 73, '何宇明', 'crm-学生详情页-课程评价 默认显示新版', 'crm-学生详情页-课程评价 默认显示新版； 老版排到最后去，新版放最前面，默认显示新版。\n排版 科目 学生上课表现，互动回答响应，作业完成情况，内容掌握情况 占的宽度缩小；后面3个的宽度加大。\n\n学生上课表现，互动回答响应，作业完成情况，内容掌握情况 这4个可以合并为1各（现/互/业/掌） 加一个问号提示 写明代表的具体意义。', 2, '2020-12-11', NULL, '2020-12-14', '2020-12-14', '2020-12-11 10:03:39', 1, 3, NULL, NULL);
