INSERT INTO `crm_request_manage` VALUES (1046, 2021, 3, '运营中心', 'H5开发', 1865, '蒋明', '红包翻倍活动', '一、需求背景：\n1、为庆祝微著13周年庆，特此开发此活动便于顾问签\n单若效果可以，后期将规划成可配置的后台，进行配\n置，做成顾问长期使用的工具。\n二、功能规划：\n1、红包的创建\n红包个数、金额、及有效期由运营部创建\n2、红包活动入口\n二维码\n3、红包有效时长、使用条件及倍率问题\n详见任务附件\n4、领取成功通知\n1、顾问端提醒\n企微推送给顾问\n2、用户端提醒\n服务号推送消息给客户\n5、数据效果统计\n需统计领取红包的用户以及使用该系列红包的订单\n三、原型地址：https://org.modao.cc/app/3681e2ea77c0afa141340e4b6875de7d6b777934?simulator_type=outside_artboard&sticky', 2, '2021-03-02', NULL, '2021-03-11', '2021-03-19', '2021-01-04 13:59:11', 1, 3, NULL, NULL);
