INSERT INTO `crm_request_manage` VALUES (1076, 2021, 1, '研发中心', 'crm', 72, '谭杰', '报表下载优化', '背景：现在的报表，下载模块只能是经理和管理员权限才能下载。下载需要单独的配置到某个人或某几个人。\n1. 在前端增加一个可以下载人员的配置项。(仅管理员能操作，互动方式，参考系统配置的人员配置，由于人员太多，移除离职的用户)。\n2. 下载的权限判定，允许不是经理权限且被配置过的普通用户下载数据。', 2, '2021-01-12', NULL, '2021-01-15', '2021-01-25', '2021-01-12 10:56:04', 1, 2, NULL, NULL);
