INSERT INTO `crm_request_manage` VALUES (1089, 2021, 1, '业务部门', '辅导君CRM', 1865, '蒋明', '签单-实付金额统计逻辑', '1、需求原因：用户通过app余额加其他付款方式，两种余额一起支付时，购买的订单课程，会导致CRM中订单的实付价格，仅统计了其他付款方式，未统计到余额支付的数值。最终导致顾问金额不能完整录入，赠送课时数缺少的情况\n2、需求描述：通过app余额购买的订单，CRM中实付价格，应按照用户实际累计支付金额统计\n3、功能规划:   CRM中签单，实付金额=余额+其他支付金额', 2, '2021-01-14', NULL, '2021-01-14', '2021-01-14', '2021-01-14 16:05:18', 1, 1, NULL, NULL);
