INSERT INTO `crm_request_manage` VALUES (1111, 2021, 1, '运营中心', 'Java项目', 1865, '蒋明', '试听客户匹配试听设备', '需求缘由：新签客户市场报表中没有客户的上试听课设备信息，所以市场无法分析各个渠道手机试听课占比。无法优化渠道。\n功能规划：将EXCEL中匹配对应的试听设备信息，输出在原EXCEL中，匹配出对应的试听设备\nEXCEL表单独传任务中', 2, '2021-01-20', NULL, '2021-01-20', '2021-01-20', '2021-01-20 10:02:48', 1, 2, NULL, NULL);
