INSERT INTO `crm_request_manage` VALUES (1125, 2021, 1, '运营中心', 'Java项目', 1865, '蒋明', '课程套餐配置同步信息', '一、需求背景：\n为方便市场部门管理配置套餐，减少人工操作错误，实现系统智能化操作。app套餐年级更改同步至CRM\n二、功能规划：\n1、新增类型：需要同步年级到：crm_course_category 里面的 grades 字段。int类型的数组格式。\n2、删除套餐：需要对应的删除年级\n\n友情提示：CRM套餐变更，还需市场手动调整', 2, '2021-01-22', NULL, '2021-01-25', '2021-01-27', '2021-01-22 11:57:04', 1, 2, NULL, NULL);
INSERT INTO `crm_request_manage` VALUES (1126, 2021, 2, '教学中心', 'CRM', 1413, '黄磊', 'crm-老师主页-老师标签数据取老师app标签的数据', 'crm-老师主页-老师标签数据取老师app-老师标签的数据：\n1.需要改动地方：1.老师个人主页、2.发起试听页面（期望老师标签）、3.crm-老师-老师检索页面、4.匹配规则-教务匹配里面的：根据老师标签加分（这个我单独建一个任务）\n2.原型：https://axhub.im/pro/3d9fede04d124c43/#g=1', 2, '2021-01-22', NULL, '2021-02-02', '2021-02-03', '2021-01-22 14:12:17', 1, 3, NULL, NULL);
